#if UNITY_IPHONEusing UnityEngine;using System.Collections;public class TouchInput : MonoBehaviour{	    // the max amount of time that can pass for a second tap to register as a double tap    public float DoubleTapDelayMax = 0.75f;		float m_fDoubleTapTimer;    float m_fOrigTwoTouchDist;    float m_fCurrTwoTouchOffset;    int m_nNumTaps;    void Start()    {		m_fDoubleTapTimer = m_fOrigTwoTouchDist = m_fCurrTwoTouchOffset = 0.0f;        m_nNumTaps = 0;    }    void Update()    {        if (m_nNumTaps > 0)		    m_fDoubleTapTimer += Time.deltaTime;           // check for other touch input besides buttons        if (!Game.Instance.MI.ButtonTouched)        {            if (Input.touchCount == 1)			{                m_fCurrTwoTouchOffset = m_fOrigTwoTouchDist = 0.0f;				Touch touch = Input.GetTouch(0);								if (touch.phase == TouchPhase.Began && m_fDoubleTapTimer < DoubleTapDelayMax)				{	                ++m_nNumTaps;				}	            else if (touch.phase == TouchPhase.Ended)	            {	                m_fDoubleTapTimer = 0.0f;	            }			}            else if (Input.touchCount == 2)            {                m_nNumTaps = 0;                Touch touch1 = Input.GetTouch(0);                Touch touch2 = Input.GetTouch(1);                if (touch1.phase == TouchPhase.Began || touch2.phase == TouchPhase.Began)                    m_fOrigTwoTouchDist = (touch2.position - touch1.position).magnitude;                else                {                    m_fCurrTwoTouchOffset = (touch2.position - touch1.position).magnitude - m_fOrigTwoTouchDist;                }            }            else if (Input.touchCount == 0)            {                m_fCurrTwoTouchOffset = m_fOrigTwoTouchDist = 0.0f;                m_nNumTaps = 0;            }        }    }    public int NumTaps    {        get { return m_nNumTaps; }    }    public float CurrTwoTouchOffset    {        get { return m_fCurrTwoTouchOffset; }    }}#endif